import React, { Component } from "react";
import Form from "./Form";
import Table from "./Table";

class App extends Component {
  state = {
    animals: []
  };

  // Code is invoked after the component is mounted/inserted into the DOM tree.
  componentDidMount() {
    const url = "http://stapi.co/api/v1/rest/comicStrip/search";

    fetch(url)
      .then(result => result.json())
      .then(result => {
        this.setState({
          animals: result.animals.slice(0, 4)
        });
      });
  }

  removeAnimal = index => {
    let { animals } = this.state;

    this.setState({
      animals: animals.filter((animal, i) => {
        return i !== index;
      })
    });
  };

  handleSubmit = animal => {
    this.setState({ animals: [...this.state.animals, animal] });
  };

  //   render() {
  //     const { data } = this.state;

  //     const result = data.map((entry, index) => {
  //       return <li key={index}>{entry.name}</li>;
  //     });

  //     return <ul>{result}</ul>;
  //   }

  render() {
    const { animals } = this.state;

    return (
      <div className="container">
        <Table animalData={animals} removeAnimal={this.removeAnimal} />
        <Form handleSubmit={this.handleSubmit} />
      </div>
    );
  }
}

export default App;
