import React from "react";

const TableHeader = () => {
  return (
    <thead>
      <tr>
        <th>Name</th>
        <th>Uid</th>
      </tr>
    </thead>
  );
};

const TableBody = props => {
  const rows = props.animalData.map((row, index) => {
    if (index === 4) {
      return 0;
    }
    return (
      <tr key={index}>
        <td>{row.name}</td>
        <td>{row.uid}</td>
        <td>
          <button onClick={() => props.removeAnimal(index)}>Delete</button>
        </td>
      </tr>
    );
  });

  return <tbody>{rows}</tbody>;
};

const Table = props => {
  const { animalData, removeAnimal } = props;

  return (
    <table>
      <TableHeader />
      <TableBody animalData={animalData} removeAnimal={removeAnimal} />
    </table>
  );
};

export default Table;
