import React, { Component } from "react";

class App extends Component {
  state = {
    animals: []
  };

  // Code is invoked after the component is mounted/inserted into the DOM tree.
  componentDidMount() {
    const url = "http://stapi.co/api/v1/rest/animal/search";

    fetch(url)
      .then(result => result.json())
      .then(result => {
        console.log(result);
        this.setState({
          data: result.animals
        });
      });
  }

  removeAnimal = index => {
    const { animals } = this.state;

    this.setState({
      characters: animals.filter((animal, i) => {
        return i !== index;
      })
    });
  };

  handleSubmit = animal => {
    this.setState({ animals: [...this.state.animals, animal] });
  };

  render() {
    const { data } = this.state;

    const result = data.map((entry, index) => {
      return <li key={index}>{entry.name}</li>;
    });

    return <ul>{result}</ul>;
  }

  render() {
    const { animals } = this.state;

    return (
      <div className="container">
        <Table characterData={animals} removeCharacter={this.removeAnimal} />
        <Form handleSubmit={this.handleSubmit} />
      </div>
    );
  }
}

export default App;
